ValidationRule.errorConditionFormula:
AND( contains( $Profile.Name, "Sales"), NOT(ISPICKVAL(Type, "Churn")), NOT(ISPICKVAL(Type, "Renewal")), NOT(ISPICKVAL(Type, "Trial")),
OR( ISBLANK(What_customer_win__c), ISBLANK(What_is_the_Opportunity__c), ISBLANK(Why_did_they_select_us_and_thanks__c), ISBLANK(TEXT(Was_this_a_competitive_deal__c)), ISBLANK(Internal_thank_yous__c), ISBLANK(TEXT(Why_did_they_select_us_picklist__c))
), CONTAINS(TEXT(StageName), "Closed Won") )
